dependencies.type:
DROP TYPE IF EXISTS gm CASCADE; CREATE FUNCTION gm_in(cstring) RETURNS gm AS '$libdir/hdgraphdb/hmm_lib', 'gm_in' LANGUAGE C IMMUTABLE STRICT;
CREATE FUNCTION gm_out(gm) RETURNS cstring AS '$libdir/hdgraphdb/hmm_lib', 'gm_out' LANGUAGE C IMMUTABLE STRICT;
CREATE FUNCTION gm_recv(internal) RETURNS gm AS '$libdir/hdgraphdb/hmm_lib', 'gm_recv' LANGUAGE C IMMUTABLE STRICT;
CREATE FUNCTION gm_send(gm) RETURNS bytea AS '$libdir/hdgraphdb/hmm_lib', 'gm_send' LANGUAGE C IMMUTABLE STRICT;
CREATE TYPE gm ( internallength = 72, input = gm_in, output = gm_out, receive = gm_recv, send = gm_send, alignment = double );


DROP TYPE IF EXISTS hmm_type CASCADE;
CREATE TYPE hmm_type AS (
gid           int,
models        gm[],
modelids      int[],
weights       float8[],
transitions   float8[],
inits         float8[]
);
